import UIKit
import Cartography

protocol HelloViewControllerInput: class {
    func displaySuccess(viewModel: Hello.Something.ViewModel)
    func displayError(errorResponse: Hello.Something.ErrorResponse)
}

class HelloViewController: UIViewController {
    // MARK: - IBOutlets
    var nameTextField: UITextField = {
        let textField = UITextField()
        textField.textAlignment = .center
        return textField
    }()
    
    // MARK: - Variables
    
    var interactor: HelloInteractorInput?
    var router: (NSObjectProtocol & HelloRoutingLogic & HelloDataPassing)?
    
    // MARK: Object lifecycle
    
    override init(nibName nibNameOrNil: String?, bundle nibBundleOrNil: Bundle?) {
        super.init(nibName: nibNameOrNil, bundle: nibBundleOrNil)
        setup()
    }
    
    required init?(coder aDecoder: NSCoder) {
        super.init(coder: aDecoder)
        setup()
    }
    
    // MARK: Setup
    
    private func setup() {
        let viewController = self
        let interactor = HelloInteractor(apiClient: APIClient())
        let presenter = HelloPresenter()
        let router = HelloRouter()
        viewController.interactor = interactor
        viewController.router = router
        interactor.presenter = presenter
        presenter.viewController = viewController
        router.viewController = viewController
        router.dataStore = interactor
    }
    
    private func setupTextField() {
        self.view.addSubview(nameTextField)
        constrain(nameTextField, self.view) { (name, superview) in
            name.centerY == superview.centerY
            name.centerX == superview.centerX
            name.leading == superview.leading + 16
            name.trailing == superview.trailing - 16
        }
    }
    
    // MARK: Routing
    
    override func prepare(for segue: UIStoryboardSegue, sender: Any?) {
        if let scene = segue.identifier {
            let selector = NSSelectorFromString("routeTo\(scene)WithSegue:")
            if let router = router, router.responds(to: selector) {
                router.perform(selector, with: segue)
            }
        }
    }
    
    // MARK: View lifecycle
    
    override func viewDidLoad() {
        super.viewDidLoad()
        setupTextField()
        doSomething()
    }
    
    // MARK: Do something
    
    func doSomething() {
        let request = Hello.Something.Request()
        interactor?.doSomething(request: request)
    }
}

extension HelloViewController: HelloViewControllerInput {
    func displaySuccess(viewModel: Hello.Something.ViewModel) {
        nameTextField.text = viewModel.fullName
    }
    
    func displayError(errorResponse: Hello.Something.ErrorResponse) {
        let alert = UIAlertController(title: "Error", message: "Something happened :(", preferredStyle: .alert)
        let action = UIAlertAction(title: "OK", style: .default, handler: nil)
        alert.addAction(action)
        self.present(alert, animated: true, completion: nil)
    }
}
